function setCookie(a,d,c){var b=new Date;b.setTime(b.getTime()+864E5*c);c="expires="+b.toUTCString();document.cookie=a+"="+d+"; "+c}function getCookie(a){a+="=";for(var d=document.cookie.split(";"),c=0;c<d.length;c++){for(var b=d[c];" "==b.charAt(0);)b=b.substring(1);if(-1!=b.indexOf(a))return b.substring(a.length,b.length)}return""}function clearCookie(a){setCookie(a,"",-1)}
function checkCookie(){var a=getCookie("username");""!=a?alert("Welcome again "+a):(a=prompt("Please enter your name:",""),""!=a&&null!=a&&setCookie("username",a,365))}function setCookieOneDay(a,d){var c=new Date;c.setDate(c.getDate()+1);c.setHours(0);c.setMinutes(0);c.setSeconds(0);var b=new Date,c=c.getTime()-b.getTime();b.setTime(b.getTime()+c);b="expires="+b.toUTCString();document.cookie=a+"="+d+"; "+b};